      const toFallback = '#'
      if (props.href) return props.href

      if (typeof props.to === 'string') return props.to || toFallback

      const to = props.to as RouteLocation

      if (
        Object.prototype.toString.call(to) === '[object Object]' &&
        (to.path || to.query || to.hash)
