      active: activeBoolean.value,
      disabled: disabledBoolean.value,
    }))

    const clicked = (e: MouseEvent): void => {
      if (disabledBoolean.value) {
        e.preventDefault()
        e.stopImmediatePropagation()
        return
      }
      emit('click', e)
    }

    return {
      computedLinkClasses,
      tag,
      routerAttr,
      link,
      clicked,
      activeBoolean,
      appendBoolean,
      disabledBoolean,
      replaceBoolean,
      exactBoolean,
    }
  },
})
</script>
