        <!-- TODO this renders incorrectly -->
        <b-button v-if="hasCloseSlot || closeContent" type="button" @click="closeClicked">
          <slot name="close">
            {{ closeContent }}
          </slot>
        </b-button>
        <b-close-button v-else :aria-label="dismissLabel" @click="closeClicked" />
      </template>
    </div>
  </b-transition>
