    noResize: {type: [Boolean, String] as PropType<Booleanish>, default: false},
    rows: {type: [String, Number], required: false, default: 2},
    wrap: {type: String, default: 'soft'},
  },
  emits: ['update:modelValue', 'change', 'blur', 'input'],
  setup(props, {emit}) {
